package com.imsajib02.passwordmanager;import android.content.BroadcastReceiver;import android.content.Context;import android.content.Intent;import android.text.TextUtils;import android.util.Log;import java.sql.Time;import java.text.SimpleDateFormat;import java.time.LocalDate;import java.util.Calendar;import java.util.Date;import java.util.Locale;import static com.imsajib02.passwordmanager.Local.*;import static com.imsajib02.passwordmanager.Support.FAILED_BACKUP;public class MyAlarmReceiver extends BroadcastReceiver {    public static final int REQUEST_CODE1 = 4004;    public static final int REQUEST_CODE2 = 4040;    @Override    public void onReceive(Context context, Intent intent)    {        Intent i = new Intent(context, ScheduleBackupService.class);        i.putExtra(FAILED_BACKUP, intent.getBooleanExtra(FAILED_BACKUP, false));        if(TextUtils.equals(getPreferenceString(context, TYPEofSCHEDULE), "Daily"))        {            context.startService(i);        }        else if(TextUtils.equals(getPreferenceString(context, TYPEofSCHEDULE), "Weekly"))        {            Calendar calendar = Calendar.getInstance();            Date date = calendar.getTime();            String currentDay = new SimpleDateFormat("EEEE", Locale.ENGLISH).format(date.getTime());            if(TextUtils.equals(getPreferenceString(context, DAYofSCHEDULE), currentDay))            {                context.startService(i);            }        }    }}